import React, { useEffect, useState } from 'react';
//import '../estilo/Register_cliente.css';

function RegistrationHotel() {
  const [hotelData, setHotelData] = useState({});

  const [formData, setFormData] = useState({
    nombre: '',
    descripcion: '',
    email: '',
    image: '',
    cant_hab: ''
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
  
    if (name === "cant_hab" && value != '') {
      const intValue = parseInt(value);
      setFormData((prevFormData) => ({
        ...prevFormData,
        [name]: intValue
      }));
    } else {
      setFormData((prevFormData) => ({
        ...prevFormData,
        [name]: value
      }));
    }
  };

  useEffect(() => {
    setHotelData('');
  
    if (formData.email) {
      fetch(`http://localhost:8090/admin/hotel/email/${formData.email}`)
        .then(response => response.json())
        .then(data => {
            setHotelData(data);
        })
        .catch(error => {
          console.error('Error al obtener los datos del cliente:', error);
        });
    }
  }, [formData.email]);

  const RegisterHotel = () => {
    if (formData.email === hotelData.email) {
      alert('El email ya pertenece a un hotel');
    }
    else if (formData.nombre === hotelData.nombre) {
      alert('El nombre no esta disponible');
    }
    else
    {
      fetch('http://localhost:8090/admin/hotel', {
      method: 'POST',
      headers: {
      'Content-Type': 'application/json'
      },
      body: JSON.stringify(formData)
      })
      .then(response => response.json())
      .then(data => {
        console.log('Registro exitoso:', data);
        window.location.href = '/ver-hoteles';
      })
      .catch(error => {
        console.error('Error en el registro:', error);
        alert('Hotel no registrado');
      });
    }
  };

  return (
    <div className="registration-container">
      <h2>Registro</h2>
      <form onSubmit={RegisterHotel} className="registration-form">
        <label>
          Nombre:
          <input
            type="text"
            name="nombre"
            value={formData.nombre}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
         Descripcion:
          <input
            type="text"
            name="descripcion"
            value={formData.descripcion}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
          Email:
          <input
            type="text"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
         Imagen:
          <input
            type="text"
            name="imagen"
            value={formData.imagen}
            onChange={handleChange}
          />
        </label>
        <br />
        <label>
          Cant_hab:
          <input
            type="text"
            name="cant_hab"
            value={formData.cant_hab}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <button type="submit">Registrar Hotel</button>
      </form>
    </div>
  );
}

export default RegistrationHotel;
