    const getUser = () => {
        if (!isLoggedCliente) {
          window.location.href = '/login-cliente';
        }
        else if (!isLoggedAdmin)
        {
          window.location.href = `/reservar/${hotelId}`;
        }
      };
  const accountDetails = {
    nombre: 'John',
    apellido: 'Doe',
    username: 'johndoe',
    password: '********',
    email: 'johndoe@example.com'
  };

  return (
    <div className="container" onLoad={getUser}>
      <div className="account-form">
        <h2>Detalles de la cuenta</h2>
        <div className="account-field">
          <p>Nombre: {accountDetails.nombre}</p>
        </div>
        <div className="account-field">
          <p>Apellido: {accountDetails.apellido}</p>
        </div>
        <div className="account-field">
          <p>UserName: {accountDetails.username}</p>
        </div>
        <div className="account-field">
          <p>Password: {accountDetails.password}</p>
        </div>
        <div className="account-field">
          <p>Email: {accountDetails.email}</p>
        </div>
        <div className="account-buttons">
          <button className="logout-button">Cerrar sesi√≥n</button>
          <button className="reservations-button">Reservas</button>
        </div>
      </div>
    </div>
  );
}

export default AccountDetails;
