import React, { useContext, useState, useEffect } from 'react';
import { AuthContext } from './auth';
import { Link } from 'react-router-dom';
import './estilo/inicio.css';

const HomePage = () => {
  const [hotels, setHotels] = useState([]);
  const { isClienteLoggedIn } = useContext(AuthContext);

  const getHotels = async () => {
    try {
      const request = await fetch("http://localhost:8090/cliente/hoteles");
      const response = await request.json();
      setHotels(response);
    } catch (error) {
      console.log("No se pudieron obtener los hoteles:", error);
    }
  };

  if (!isClienteLoggedIn()) {
    // Redirige al cliente a la página de inicio de sesión si no está logeado
    return <Redirect to="/login" />;
  }

  useEffect(() => {
    getHotels();
  }, []);

  return (
    <div className="container">
      <div className="admin-button-container">
        <Link to="/admin" className="admin-button">
          Admin
        </Link>
      </div>
      <div className="hotels-container">
        {hotels.length ? (
          hotels.map((hotel) => (
            <div className='hotel-card' key={hotel.id}>
              <img src={hotel.image} alt={hotel.nombre}></img>
              <h4>{hotel.nombre}</h4>
              <p>{hotel.email}</p>
                <button onClick={verificacion}>Reservar</button>
            </div>
          ))
        ) : (
          <p>No hay hoteles</p>
        )}
      </div>
    </div>
  );
};

export default HomePage;
