import React, { useContext, useEffect, useState } from 'react';
import { AuthContext } from './login/auth';
import './estilo/reservas_cliente.css';

const HomePage = () => {
  const [reservations, setReservations] = useState([]);
  const [hoteles, setHoteles] = useState([]);
  const { isLoggedCliente } = useContext(AuthContext);

  const getReservationsAndHoteles = async () => {
    try {
      if (isLoggedCliente) {
        const accountId = localStorage.getItem("id_cliente");
        const reservationsRequest = await fetch(`http://localhost:8090/cliente/reservas/${accountId}`);
        const reservationsResponse = await reservationsRequest.json();
        setReservations(reservationsResponse);

        const hotelesArray = [];
        for (let i = 0; i < reservationsResponse.length; i++) {
          const reserva = reservationsResponse[i];
          const hotelRequest = await fetch(`http://localhost:8090/cliente/hotel/${reserva.hotel_id}`);
          const hotelResponse = await hotelRequest.json();
          hotelesArray.push(hotelResponse);
        }
        setHoteles(hotelesArray);
      } else {
        window.location.href = '/';
      }
    } catch (error) {
      console.log("Error al obtener las reservas y los hoteles:", error);
    }
  };

  useEffect(() => {
    getReservationsAndHoteles();
  }, [isLoggedCliente]);

  return (
    <div className="reservations-container1">
      <div className="reservations-container2">
        <h4>Datos de tus reservas:</h4>
        {reservations.length ? (
          reservations.map((reservation) => {
            const hotel = hoteles.find((hotel) => hotel.id === reservation.hotel_id);
            const fechaInicio = `${reservation.dia_inicio}/${reservation.mes_inicio}/${reservation.anio_inicio}`;
            const fechaFin = `${reservation.dia_final}/${reservation.mes_final}/${reservation.anio_final}`;
            return (
              <div className="reservation-card" key={reservation.ID}>
                <p>Hotel: {hotel ? hotel.nombre : 'Hotel desconocido'}</p>
                <p>Fecha de llegada: {fechaInicio}</p>
                <p>Fecha de fin: {fechaFin}</p>
                <p>Gracias por elegirnos!</p>
              </div>
            );
          })
        ) : (
          <p>No tienes reservas</p>
        )}
      </div>
    </div>
  );
};

export default HomePage;
