import React, { useContext, useEffect, useState } from 'react';
import { AuthContext } from './login/auth';
import { useParams } from 'react-router-dom';
import './estilo/reservar.css';

const ReservaPage = () => {
  const { hotelId } = useParams();
  const [hotelData, setHotelData] = useState('');
  const { isLoggedCliente } = useContext(AuthContext);
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const accountId = localStorage.getItem("id_cliente");

  const Verificacion = () => {
    if (!isLoggedCliente) {
      window.location.href = '/login-cliente';
    }
  };

  const handleReserva = () => {
    alert(hotelId);
    alert(accountId);
    alert(startDate);
    alert(endDate);
    const startDateObj = new Date(startDate);
    const endDateObj = new Date(endDate);
    const Dias = endDateObj-startDateObj;
    const [formData] = {
      hotel_id: hotelId,
      cliente_id: accountId,
      fecha_inicio: startDateObj.getTime(),
      fecha_final: endDateObj.getTime(),
      dias: Dias.toString()
    };
    alert(formData.hotel_id);
    alert(formData.cliente_id);
    alert(formData.fecha_inicio);
    alert(formData.fecha_final);
    alert(formData.dias);

    fetch('http://localhost:8090/cliente/reserva', {
      method: 'POST',
      headers: {
      'Content-Type': 'application/json'
      },
      body: JSON.stringify(formData)
      })
      .then(response => response.json())
      .then(data => {
        console.log('Registro exitoso:', data);
        window.location.href = '/';
      })
      .catch(error => {
        console.error('Error en el registro:', error);
        alert('Credenciales incorrectas');
      });
  };

  useEffect(() => {
    setHotelData('');
    if (hotelId) {
      fetch(`http://localhost:8090/cliente/hotel/${hotelId}`)
      .then(response => response.json())
      .then(data => {
        setHotelData(data);
      })
      .catch(error => {
        console.error('Error al obtener los datos del cliente:', error);
      });
    }
  }, [hotelId]);

  const handleStartDateChange = (event) => {
    setStartDate(event.target.value);
    const startDateObj = new Date(event.target.value);
    const endDateObj = new Date(endDate);
    if (startDateObj > endDateObj) {
      setEndDate('');
      alert("Fechas no validas");
    }
    if (startDate && endDate) {
      filterHotels();
    }
  };

  const handleEndDateChange = (event) => {
    setEndDate(event.target.value);
    const startDateObj = new Date(startDate);
    const endDateObj = new Date(event.target.value);
    if (startDateObj > endDateObj) {
      setEndDate('');
      alert("Fechas no validas");
    }
    if (startDate && endDate) {
      filterHotels();
    }
  };

  const filterHotels = async () => {
    const request = await fetch(`http://localhost:8090/cliente/disponibilidad/${hotelId}/${startDate}/${endDate}`);
    const response = await request.json();
    if (response === 0) {
      setEndDate('');
      alert("No hay habitaciones disponibles para esas fechas");
    }
  };

  return (
    <body className="bodyReserva">
    <div>
      {typeof hotelData === 'undefined' ? (
        <>CARGANDO...</>
      ) : (
        
        <div className="container45" onLoad={Verificacion}>
          <div className="informacion">
          <div className="cuadroImag" ><img src={hotelData.image} alt={hotelData.nombre} className="tamanoImag" /></div>
          <div className= "descripcion">{hotelData["descripcion"]}</div>
          </div>
          <div className="reserva-form">
            <h6>Realice reserva del Hotel</h6>
            <h6>{hotelData["nombre"]}</h6>
            <form onSubmit={handleReserva}>
                <div className="form-group">
                <label htmlFor="fechaInicio">Fecha de inicio:</label>
                <input
                  type="date"
                  id="fechaInicio"
                  value={startDate}
                  onChange={handleStartDateChange}
                  required
                />
                 </div>
               <div className="form-group">
                <label htmlFor="fechaFin">Fecha de fin:</label>
                <input
                  type="date"
                  id="fechaFin"
                  value={endDate}
                  onChange={handleEndDateChange}
                  required
                />
              </div>
              <button type="submit" className="confReserva">Confirmar</button>
            </form>
          </div>
        </div>
        
      )}
    </div>
    </body>
  );
};

export default ReservaPage;
