import React, { useContext, useEffect, useState } from 'react';
import { AuthContext } from './login/auth';
import { Link } from 'react-router-dom';
import './estilo/inicio.css';

const HomePage = () => {
  const [hotels, setHotels] = useState([]);
  const [reservations, setReservations] = useState([]);
  const { isLogged, user } = useContext(AuthContext); // Obtener la información del usuario autenticado

  const getHotels = async () => {
    try {
      const request = await fetch("http://localhost:8090/cliente/hoteles");
      const response = await request.json();
      setHotels(response);
    } catch (error) {
      console.log("No se pudieron obtener los hoteles:", error);
    }
  };

  const getReservations = async () => {
    try {
      const request = await fetch(`http://localhost:8090/cliente/reservas/${user.id}`); // Realizar una solicitud al backend pasando el ID del cliente autenticado
      const response = await request.json();
      setReservations(response);
    } catch (error) {
      console.log("No se pudieron obtener las reservas:", error);
    }
  };

  useEffect(() => {
    getHotels();
    getReservations();
  }, []);

  const Verificacion = (hotelId) => {
    if (!isLogged) {
      window.location.href = '/login-cliente';
    } else {
      window.location.href = `/reservar/${hotelId}`;
    }
  };

  return (
    <body className="bodyinicio">
      <div className="header-content"> </div>
      <div className="date-pickerINI">
        <label htmlFor="start-date">Fecha de inicio:</label>
        <input type="date" id="start-date" />
      </div>
      <div className="date-pickerINI">
        <label htmlFor="end-date">Fecha de fin:</label>
        <input type="date" id="end-date" />
      </div>
      <button className="search-button">Buscar</button>
      <div className="containerIni">
        <div className="admin-button-container">
          <Link to="/admin" className="admin-button">
            Admin
          </Link>
        </div>
        <div className="hotels-container">
          {hotels.length ? (
            hotels.map((hotel) => (
              <div className="hotel-card" key={hotel.id}>
                <img src={hotel.image} alt={hotel.nombre}></img>
                <h4>{hotel.nombre}</h4>
                <p>{hotel.email}</p>
                <button onClick={() => Verificacion(hotel.id)}>Reservar</button>
              </div>
            ))
          ) : (
            <p>No hay hoteles</p>
          )}
        </div>
        <div className="reservations-container">
          <h2>Tus reservas:</h2>
          {reservations.length ? (
            reservations.map((reservation) => (
              <div className="reservation-card" key={reservation.id}>
                <p>Hotel: {reservation.hotel}</p>
                <p>Fecha de inicio: {reservation.fechaInicio}</p>
                <p>Fecha de fin: {reservation.fechaFin}</p>
              </div>
            ))
          ) : (
            <p>No tienes reservas</p>
          )}
        </div>
      </div>
    </body>
  );
};

export default HomePage;
