import React, { useContext, useEffect, useState } from 'react';
import { AuthContext } from './login/auth';
import { useParams } from 'react-router-dom';
import './estilo/imagenes_hotel.css';

function RegistrationHotel() {
  const { hotelId } = useParams();
  const [hotelData, setHotelData] = useState('');
  const { isLoggedAdmin } = useContext(AuthContext);
  const [imagenes, setImagenes] = useState([]);

  const Verificacion = () => {
    if (!isLoggedAdmin) {
      window.location.href = '/login-admin';
    }
  };

  useEffect(() => {
    if (hotelId) {
      fetch(`http://localhost:8090/admin/hotel/${hotelId}`)
        .then(response => response.json())
        .then(data => {
          setHotelData(data);
        })
        .catch(error => {
          console.error('Error al obtener los datos del hotel:', error);
        });
    }
  }, [hotelId]);

  useEffect(() => {
    if (hotelId) {
      fetch(`http://localhost:8090/admin/imagenes/hotel/${hotelId}`)
        .then(response => response.json())
        .then(data => {
          setImagenes(data);
        })
        .catch(error => {
          console.error('Error al obtener los datos del hotel:', error);
        });
    }
  }, [hotelId]);

  const handleChange = event => {
    const files = event.target.files;
    const imagenesArray = [];

    for (let i = 0; i < array.length; i++) {
        const element = array[i];
        
    }

    for (let i = 0; i < files.length; i++) {
      const reader = new FileReader();
      reader.onload = () => {
        imagenesArray.push(reader.result);
        if (i === files.length - 1) {
          setImagenes(imagenesArray);
        }
      };
      reader.readAsDataURL(files[i]);
    }
  };

  const AgregarImagenes = async () => {
    if (imagenes.length > 0) {
      const formDataWithImages = new FormData();
      for (let i = 0; i < imagenes.length; i++) {
        formDataWithImages.append('imagen', imagenes[i]);
      }

      const req = await fetch(
        `http://localhost:8090/admin/hotel/imagen/${hotelId}`,
        {
          method: 'PUT',
          body: formDataWithImages
        }
      );
      const res = await req.json();

      if (req.ok) {
        window.location.href = '/editar-hoteles';
      } else {
        console.error('Error en el registro:', res);
        alert('Imagen no registrada');
      }
    } else {
      window.location.href = '/editar-hoteles';
    }
  };

  return (
    <div className="registration-container" onLoad={Verificacion}>
      <h2 className="nombre">{hotelData['nombre']}</h2>
      <form onSubmit={AgregarImagenes} className="registration-form">
        <label>
          Imagen:
          <input type="file" name="imagen" onChange={handleChange} multiple />
        </label>
        <div className="image-grid">
          {imagenes.map((imagen, index) => (
            <img key={index} src={`http://localhost:8090/${imagen.url}`} alt={`Imagen ${index}`} />
          ))}
        </div>
        <button type="submit">Guardar Im√°genes</button>
      </form>
    </div>
  );
}

export default RegistrationHotel;
