import React, { useContext, useEffect, useState } from 'react';
import { AuthContext } from './login/auth';
import { useParams } from 'react-router-dom';
import './estilo/reservar.css';

const ReservaPage = () => {
  const { hotelId } = useParams();
  const [hotelData, setHotelData] = useState('');
  const [fechaInicio, setFechaInicio] = useState('');
  const [fechaFin, setFechaFin] = useState('');
  const [cantidadPersonas, setCantidadPersonas] = useState('');
  const [commodities, setCommodities] = useState('');
  const { isLogged } = useContext(AuthContext);

  if (!isLogged) {
    window.location.href = '/login-cliente';
  }
  else
  {
    window.location.href = `/reservar/${hotelId}`;
  }

  const handleReserva = () => {
    
  };

  useEffect(() => {
    setHotelData('');

    if (hotelId) {
      fetch(`http://localhost:8090/cliente/hotel/${hotelId}`)
        .then(response => response.json())
        .then(data => {
          setHotelData(data);
        })
        .catch(error => {
          console.error('Error al obtener los datos del cliente:', error);
        });
    }
  }, [hotelId]);

  return (
    <div>
      {typeof hotelData === 'undefined' ? (
        <>CARGANDO...</>
      ) : (
        <div className="container" on>
          <div className="reserva-form">
            <h2>Reserva de Hotel</h2>
            <h3>{hotelData["nombre"]}</h3>
            <img src="ruta/de/la/foto.jpg" alt="Foto del Hotel" />
            <form onSubmit={handleReserva}>
              <div className="form-group">
                <label htmlFor="fechaInicio">Fecha de inicio:</label>
                <input
                  type="date"
                  id="fechaInicio"
                  value={fechaInicio}
                  onChange={(e) => setFechaInicio(e.target.value)}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="fechaFin">Fecha de fin:</label>
                <input
                  type="date"
                  id="fechaFin"
                  value={fechaFin}
                  onChange={(e) => setFechaFin(e.target.value)}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="cantidadPersonas">Cantidad de personas:</label>
                <input
                  type="number"
                  id="cantidadPersonas"
                  value={cantidadPersonas}
                  onChange={(e) => setCantidadPersonas(e.target.value)}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="commodities">Commodities:</label>
                <textarea
                  id="commodities"
                  value={commodities}
                  onChange={(e) => setCommodities(e.target.value)}
                  required
                ></textarea>
              </div>
              <button type="submit">Confirmar</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default ReservaPage;
