// auth.js

import React, { createContext, useState } from 'react';

// Crea un contexto para compartir el estado de autenticación
export const AuthContext = createContext();

// Componente de proveedor de autenticación
export const AuthProvider = ({ children }) => {
  const [token, setToken] = useState(localStorage.getItem('token') || '');

  // Función para realizar el inicio de sesión
  const login = (newToken) => {
    setToken(newToken);
    localStorage.setItem('token', newToken);
  };

  // Función para realizar el cierre de sesión
  const logout = () => {
    setToken('');
    localStorage.removeItem('token');
  };

  // Función para verificar si el cliente está logeado
  const isClienteLoggedIn = () => {
    return !!token;
  };

  // Proporciona el estado de autenticación y las funciones relacionadas a los componentes hijos
  return (
    <AuthContext.Provider value={{ token, login, logout, isClienteLoggedIn }}>
      {children}
    </AuthContext.Provider>
  );
};
