export const isLoggedIn = () => {
    // Verifica si el cliente está logeado
    // Puedes implementar tu propia lógica de autenticación aquí
  
    // Ejemplo:
    const loggedIn = localStorage.getItem('clienteToken') !== null;
  
    return loggedIn;
  };


export const isClienteLoggedIn = () => {
    const clienteToken = localStorage.getItem('clienteToken');
    return clienteToken !== null;
  };
