import React, { useContext, useEffect, useState } from 'react';
import { AuthContext } from './login/auth';
import './estilo/ver_reservas.css';

const VerReservas = () => {
  const [reservas, setReservas] = useState([]);
  const [hoteles, setHoteles] = useState([]);
  const { isLoggedAdmin } = useContext(AuthContext);

  const getHoteles = useCallback(async () => {
    try {
      const hotelesArray = [];
      for (let i = 0; i < reservations.length; i++) {
        const reserva = reservations[i];
        const request = await fetch(`http://localhost:8090/cliente/hotel/${reserva.hotel_id}`);
        const response = await request.json();
        hotelesArray.push(response);
      }
      setHoteles(hotelesArray);
    } catch (error) {
      console.log("No se pudieron obtener los hoteles:", error);
    }
  }, [reservations]);

  const getReservations = useCallback(async () => {
    if (isLoggedAdmin) {
      const accountId = localStorage.getItem("id_admin");
      try {
        const request = await fetch(`http://localhost:8090/cliente/reservas/${accountId}`);
        const response = await request.json();
        setReservations(response);
      } catch (error) {
        console.log("No se pudieron obtener las reservas:", error);
      }
      finally {
        getHoteles();
      }
    } else {
      window.location.href = '/';
    }
  }, [isLoggedAdmin, getHoteles]);

  useEffect(() => {
    getReservations();
  }, [getReservations]);

  return (
    <body className="bodyinicio">
      <div className="containerIni">
        <div className="reserva-container">
          {reservas.length ? (
            reservas.map((reserva) => (
              <div className="reserva-card" key={reserva.id}>
                <img src={reserva.nombre} alt={reserva.nombre} className="hotel-image" />
                <div className="reserva-info">
                  <h4>{reserva.nombre}</h4>
                  <p>{reserva.email}</p>
                </div>
                <div className="cliente-info">
                  <p>{reserva.name}</p>
                  <p>{reserva.last_name}</p>
                </div>
                <div className="fechas-info">
                  <p>{reserva.fecha_inicio}</p>
                  <p>{reserva.fecha_final}</p>
                  <p>{reserva.dias}</p>
                </div>
              </div>
            ))
          ) : (
            <p>No hay reservas</p>
          )}
        </div>
      </div>
    </body>
  );
};

export default VerReservas;
