import React, { useContext, useEffect, useState } from 'react';
import { AuthContext } from './login/auth';
//import { Link } from 'react-router-dom';
import './estilo/inicio.css';

const HomePage = () => {
  //const [hotels, setHotels] = useState([]);
  const [reservations, setReservations] = useState([]);
  const { isLoggedCliente } = useContext(AuthContext);
  const [accountId, setAccountId] = useState('');

  useEffect(() => {
    const getUser = () => {
      if (isLoggedCliente) {
        setAccountId(localStorage.getItem("id_cliente"))
      }
      else {
        window.location.href = '/';
      }
    };

    getUser();
  }, [isLoggedCliente]);

  const getReservations = async () => {
    try {
      const request = await fetch(`http://localhost:8090/cliente/reservas/${accountId}`); // Realizar una solicitud al backend pasando el ID del cliente autenticado
      const response = await request.json();
      setReservations(response);
    } catch (error) {
      console.log("No se pudieron obtener las reservas:", error);
    }
  };

  useEffect(() => {
    getReservations();
  }, []);

  return (
   
        <div className="reservations-container" onLoad={getReservations}>
          <h4>Tus reservas:</h4>
          {reservations.length ? (
            reservations.map((reservation) => (
              <div className="reservation-card" key={reservation.id}>
                <p>Hotel: {reservation.hotel}</p>
                <p>Fecha de inicio: {reservation.fechaInicio}</p>
                <p>Fecha de fin: {reservation.fechaFin}</p>
              </div>
            ))
          ) : (
            <p>No tienes reservas</p>
          )}
        </div>
    
  );
};

export default HomePage;
