import React, { createContext, useState } from 'react';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [token, setToken] = useState(localStorage.getItem('token') || '');
    const [isLoggedCliente, setIsLoggedCliente] = useState(localStorage.getItem('auth') ? true : false);
    const [isLoggedAdmin, setIsLoggedAdmin] = useState(localStorage.getItem('auth') ? true : false);

  const loginAdmin = (newToken) => {
    setIsLoggedAdmin(true);
    setToken(newToken);
    localStorage.setItem('token', token);
    localStorage.setItem('id_admin', token);
    localStorage.setItem('auth', true);
  };

  const loginCliente = (newToken) => {
    setIsLoggedCliente(true);
    setToken(newToken);
    localStorage.setItem('token', token);
    localStorage.setItem('id_cliente', token);
    localStorage.setItem('auth', true);
  };

  const logout = () => {
    setIsLoggedCliente(false);
    setIsLoggedAdmin(false);
    setToken('');
    localStorage.removeItem('token');
    localStorage.setItem('auth', false);
  };

  const propiedades = {
    isLoggedCliente,
    isLoggedAdmin,
    loginCliente,
    loginAdmin,
    logout,
  };

  return (
    <AuthContext.Provider value={propiedades}>
      {children}
    </AuthContext.Provider>
  );
};
